import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:8000/tasks/', {
          params: {
            user: 'sai',
          },
        });
        const adminTasks = Object.values(response.data).filter(task => task.user === Cookies.get('Admin') );

    
        setTasks(adminTasks);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };
    
    fetchTasks();
  }, []);

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:8000/tasks/${id}/`);
    setTasks((prevTasks) => prevTasks.filter((task) => task.id !== id));
  };

  const handleCheckboxChange = async (id, completed) => {
    // Find the task with the specified id
    const updatedTasks = tasks.map((task) =>
      task.id === id ? { ...task, completed: completed === '0' ? '1' : '0' } : task
    );

    console.log(Cookies.get('Admin') )

    setTasks(updatedTasks);

    try {
      // Find the specific task for the provided id
      const selectedTask = tasks.find((task) => task.id === id);

      // Update the 'completed' field in the database
      const response = await axios.put(`http://localhost:8000/tasks/${id}/`, {
        completed: completed === '0' ? '1' : '0',
        user: Cookies.get('Admin'),
        title: selectedTask.title,
        description: selectedTask.description,
        due_date: selectedTask.due_date,
      });

      console.log('Update successful. Server response:', response.data);
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  

  
  const exampleModal = document.getElementById('exampleModal');
const Description = document.getElementById('Description');
const due_date = document.getElementById('duedate');
const  taskId = document.getElementById('duedate');

if (exampleModal) {
  exampleModal.addEventListener('show.bs.modal', event => {
    const button = event.relatedTarget;
    const recipient = button.getAttribute('data-bs-whatever');
    const taskId = button.getAttribute('data-bs-taskId');
    const description = button.getAttribute('data-bs-description');
    const dueDate = button.getAttribute('data-bs-due_date');
  
    const modalTitle = exampleModal.querySelector('.modal-title');
    const modalBodyInput = exampleModal.querySelector('.modal-body input');
    const task_Id = document.getElementById('taskId');
  
    modalTitle.textContent = `Update Task`;
    modalBodyInput.value = recipient;
  
    Description.value = description;
    due_date.value = dueDate;
    task_Id.value = taskId;  // Corrected to use taskId
  
    // Set taskId when modal is opened
    setTaskId(button.getAttribute('data-bs-id'));
  });
}







const handleSave = async () => {
  try {
    // Get values from the modal form
    const title = document.getElementById('Title').value;
    const description = document.getElementById('Description').value;
    const due_date = document.getElementById('duedate').value;

    // Find the specific task for the provided id
    const selectedTask = tasks.find((task) => task.id === taskId);

    // Update the task in the database
    const response = await axios.put(`http://localhost:8000/tasks/${selectedTask.id}/`, {
      completed: selectedTask.completed,
      user: Cookies.get('Admin'),
      title,
      description,
      due_date,
    });

    console.log('Update successful. Server response:', response.data);

    // Close the modal after saving
    const modal = new bootstrap.Modal(document.getElementById('exampleModal'));
    modal.hide();
  } catch (error) {
    console.error('Error updating task:', error);
  }
};





  return (
        <div>
            <table className="table table-sm">
                <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Title</th>
                        <th scope="col">Description</th>
                        <th scope="col">Due Date</th>
                        <th scope="col">Delete</th>
                        <th scope="col">Completed</th>
                        <th scope="col">Update</th>
                    </tr>
                </thead>
                <tbody>
                {tasks && tasks.map((task,index) => (
                        <tr key={task.id}>
                            <th scope="row" className='py-4'>{index + 1}</th>
                            <td className='py-4'>{task.title}</td>
                            <td className='py-4'>{task.description}</td>
                            <td className='py-4'>{task.due_date}</td>
                            <td className='py-4'>
                                <button type="button" className="btn btn-danger" onClick={() => handleDelete(task.id)}>X</button>
                            </td>
                            <td className='py-4'>
                                <div className="form-check form-switch">
                                    <input
                                        className="form-check-input"
                                        type="checkbox"
                                        role="switch"
                                        id={`flexSwitchCheckChecked_${task.id}`}
                                        checked={task.completed === '1'}
                                        onChange={() => handleCheckboxChange(task.id, task.completed)}
                                    />
                                </div>
                            </td>
                            <td className='py-4'>
                            <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal" data-bs-id={task.id} data-bs-whatever={task.title} data-bs-description={task.description} data-bs-due_date={task.due_date}>Open modal for @mdo</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel">Update Task</h1>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form>
        <input type="text" class="form-control" id="taskId"/>
          <div class="mb-3">
            <label for="Title" class="col-form-label">Title:</label>
            <input type="text" class="form-control" id="Title"/>
          </div>
          <div class="mb-3">
            <label for="Description" class="col-form-label">Description:</label>
            <textarea class="form-control" id="Description"></textarea>
          </div>
          <div class="mb-3">
            <label for="duedate" class="col-form-label">Due date:</label>
            <input type="date" class="form-control" id="duedate"/>
          </div>
        </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary" onClick={handleSave}>Save</button>
      </div>
    </div>
  </div>
</div>
        </div>
    );
};

export default TaskList;
