import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate  } from 'react-router-dom';
import TaskForm from './TaskForm';
import TaskList from './TaskList';
import Login from './Login';
import Signup from './signup';
import './style.css';
import axios from 'axios';
import Cookies from 'js-cookie';

function App() {
  const [tasks, setTasks] = useState([]);
  const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(Cookies.get('Admin') != null);
  const [loading, setLoading] = useState(true); // New loading state

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Simulate loading tasks from an API
        const response = await axios.get('http://localhost:8000/tasks/');
        setTasks(response.data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      } finally {
        setLoading(false); // Set loading to false after tasks are loaded or if there's an error
      }
    };
    setIsAdminLoggedIn(Cookies.get('Admin') != null);
    fetchData();

  }, []);

  const handleLogout = () => {
    Cookies.remove('Admin');
    setIsAdminLoggedIn(false);
  };

  const handleSubmit = async (title, description, due_date) => {
    const response = await axios.post('http://localhost:8000/tasks/', { title, description, due_date });
    setTasks([...tasks, response.data]);
   };





const [input, setInput] = useState(''); 
const [weather, setWeather] = useState({ 
    loading: false, 
    data: {}, 
    error: false, 
}); 

const toDateFunction = () => { 
    const months = [ 
        'January', 
        'February', 
        'March', 
        'April', 
        'May', 
        'June', 
        'July', 
        'August', 
        'September', 
        'October', 
        'November', 
        'December', 
    ]; 
    const WeekDays = [ 
        'Sunday', 
        'Monday', 
        'Tuesday', 
        'Wednesday', 
        'Thursday', 
        'Friday', 
        'Saturday', 
    ]; 
    const currentDate = new Date(); 
    const date = `${WeekDays[currentDate.getDay()]} ${currentDate.getDate()} ${months[currentDate.getMonth()] 
        }`; 
        console.log(date);
    return date; 
}; 

const search = async (event) => { 
    if (event.key === 'Enter') { 
        event.preventDefault(); 
        setInput(''); 
        setWeather({ oading: true }); 
        const url = 'https://api.openweathermap.org/data/2.5/weather'; 
        const api_key = 'f00c38e0279b7bc85480c3fe775d518c'; 
        await axios 
            .get(url, { 
                params: { 
                    q: input, 
                    units: 'metric', 
                    appid: api_key, 
                }, 
            }) 
            .then((res) => { 
                console.log('res', res); 
                setWeather({ data: res.data, loading: false, error: false }); 
            }) 
            .catch((error) => { 
                setWeather({ ...weather, data: {}, error: true }); 
                setInput(''); 
                console.log('error', error); 
            }); 
    } 
}; 


  return (
    <Router>
      <div className="App container mt-5">

      {loading ? (
          <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
            <div className="spinner-border" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        )
      :
      <Link to="/" className='ms-4'>
      <div class="container">
 <header class="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between py-3 mb-4 border-bottom">
   <div class="col-md-3 mb-2 mb-md-0">
     <a href="/" class="d-inline-flex link-body-emphasis text-decoration-none fs-1">
       SS
     </a>
   </div>

   <ul class="nav col-12 col-md-auto mb-2 justify-content-center mb-md-0">
     <li><h4 class=" px-2 ">Task Management System</h4></li>
   </ul>


   <div class="modal fade" id="weatherApp" tabindex="-1" aria-labelledby="weatherAppLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="weatherAppLabel">Modal title</h1>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
      <div className="search-bar"> 
                <input 
                    type="text"
                    className="city-search"
                    placeholder="Enter City Name.."
                    name="query"
                    value={input} 
                    onChange={(event) => setInput(event.target.value)} 
                    onKeyPress={search} 
                /> 
            </div> 
     <span className='text-center '>
     {weather.loading && ( 
                <> 
                    <br /> 
                    <br /> 
                    <Oval type="Oval" color="black" height={100} width={100} /> 
                </> 
            )} 
            {weather.error && ( 
                <> 
                    <br /> 
                    <br /> 
                    <span className="error-message"> 
                        <span style={{ fontSize: '20px' }}>City not found</span> 
                    </span> 
                </> 
            )} 
            {weather && weather.data && weather.data.main && ( 
                <div> 
                    <div className="city-name mt-3 pt-3"> 
                        <h2> 
                            {weather.data.name}, <span>{weather.data.sys.country}</span> 
                        </h2> 
                    </div> 
                    <div className="date"> 
                        <span>{toDateFunction()}</span> 
                    </div> 
                    <div className="icon-temp"> 
                        <img 
                            className=""
                            src={`https://openweathermap.org/img/wn/${weather.data.weather[0].icon}@2x.png`} 
                        /> 
                        {Math.round(weather.data.main.temp)} 
                        <sup className="deg">°C</sup> 
                    </div> 
                    <div className="des-wind"> 
                        <p>{weather.data.weather[0].description.toUpperCase()}</p> 
                        <p>Wind Speed: {weather.data.wind.speed}m/s</p> 
                    </div> 
                </div> 
            )} 
     </span>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>

   {
         isAdminLoggedIn ? 
         <div class="col-md-3 text-end">
        <button type="button" class="btn btn-info me-2 text-white"  data-bs-toggle="modal" data-bs-target="#weatherApp">Weather App</button>
 <button type="button" class="btn btn-outline-primary me-2" onClick={handleLogout}>Logout</button>
       </div>
        : 
         <div class="col-md-3 text-end">
        <button type="button" class="btn btn-info me-2 text-white"  data-bs-toggle="modal" data-bs-target="#weatherApp">Weather App</button>
